from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.common.by import By
from selenium.webdriver.support import expected_conditions as EC
import  time
from pymongo import MongoClient

driver = webdriver.Chrome()
driver.get('https://m.mail.ru/login')
assert "–í—Ö–æ–¥ ‚Äî –ü–æ—á—Ç–∞ Mail.Ru" in driver.title
elem = driver.find_element_by_name('Login')
elem.send_keys("study.ai_172@mail.ru")
elem = driver.find_element_by_name('Password')
elem.send_keys('NewPassword172')
elem.send_keys(Keys.RETURN)
elems = driver.find_elements_by_xpath("//a[@class='messageline__link']")
mail_list = []
link_list = []

for elem in elems:
    l = elem.get_attribute('href')
    link_list.append(l)

for elem in link_list[:10]:
    driver.get(elem)
    mail_voc = {}
    try:
        WebDriverWait(driver, 10).until(EC.visibility_of_all_elements_located((By.ID, "body")))
    except:
         break
    mail_from = driver.find_element_by_class_name('readmsg__text-container__inner-line').text
    mail_voc['from'] = mail_from
    mail_date = driver.find_element_by_class_name('readmsg__mail-date').text
    mail_voc['date'] = mail_date
    mail_title = driver.find_element_by_class_name('readmsg__theme').text
    mail_voc['title'] = mail_title
    mail_text = driver.find_element_by_id('readmsg__body').text
    mail_voc['text'] = mail_text
    mail_list.append(mail_voc)

driver.quit()
# print(mail_list)

client = MongoClient('localhost', 27017)
for item in mail_list:
    try:
        client['mailru']['mails'].insert_one(item)
    except DuplicateKeyError:
        pass

